import React, { useState, useEffect, useRef } from 'react';
import GlassyBlob from './GlassyBlob';
import { useAudioAnalyzer } from './AudioAnalyzer';

const JarvisInterface = () => {
  const [mode, setMode] = useState('idle');
  const [intensity, setIntensity] = useState(0.5);
  const [userText, setUserText] = useState('');
  const [assistantText, setAssistantText] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [conversationHistory, setConversationHistory] = useState([]);
  const [currentResponse, setCurrentResponse] = useState('');
  const [micEnabled, setMicEnabled] = useState(false);
  
  const wsRef = useRef(null);
  const responseRef = useRef('');

  // Audio analyzer for real-time audio level monitoring
  const { startListening, stopListening, isListening, audioLevel } = useAudioAnalyzer(
    (level) => {
      if (mode === 'listening') {
        setIntensity(Math.max(0.1, level * 1.5)); // Scale and ensure minimum
      }
    }
  );

  // WebSocket connection to Python Jarvis backend
  useEffect(() => {
    connectToJarvis();
    
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
      stopListening();
    };
  }, []);

  const connectToJarvis = () => {
    try {
      wsRef.current = new WebSocket('ws://localhost:8765');
      
      wsRef.current.onopen = () => {
        console.log('Connected to Jarvis backend');
        setIsConnected(true);
      };

      wsRef.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        handleJarvisMessage(data);
      };

      wsRef.current.onclose = () => {
        console.log('Disconnected from Jarvis backend');
        setIsConnected(false);
        // Try to reconnect after 3 seconds
        setTimeout(connectToJarvis, 3000);
      };

      wsRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setIsConnected(false);
      };
    } catch (error) {
      console.error('Failed to connect to Jarvis:', error);
      setIsConnected(false);
    }
  };

  const handleJarvisMessage = (data) => {
    switch (data.type) {
      case 'user_text':
        setUserText(data.text);
        addToConversation('user', data.text);
        break;
        
      case 'assistant_stream':
        responseRef.current += data.text;
        setCurrentResponse(responseRef.current);
        break;
        
      case 'assistant_final':
        setAssistantText(data.text);
        addToConversation('assistant', data.text);
        responseRef.current = '';
        setCurrentResponse('');
        setMode('idle');
        setIntensity(0.5);
        break;
        
      case 'mode_change':
        setMode(data.mode);
        if (data.mode === 'listening') {
          setIntensity(0.3);
          if (micEnabled) {
            startListening();
          }
        } else if (data.mode === 'speaking') {
          setIntensity(data.intensity || 0.8);
          stopListening();
        } else {
          setIntensity(0.5);
          stopListening();
        }
        break;
        
      case 'audio_level':
        if (data.mode === 'speaking') {
          setIntensity(Math.max(0.3, data.level * 1.2));
        }
        break;
        
      default:
        console.log('Unknown message type:', data.type);
    }
  };

  const addToConversation = (role, text) => {
    setConversationHistory(prev => [...prev, { role, text, timestamp: new Date().toLocaleTimeString() }]);
  };

  const sendTextMessage = () => {
    if (userText.trim() && wsRef.current && isConnected) {
      wsRef.current.send(JSON.stringify({
        type: 'text_input',
        text: userText.trim()
      }));
      
      setMode('thinking');
      setTimeout(() => setMode('speaking'), 500);
      setUserText('');
    }
  };

  const toggleMicrophone = async () => {
    if (!micEnabled) {
      try {
        await startListening();
        setMicEnabled(true);
        if (wsRef.current && isConnected) {
          wsRef.current.send(JSON.stringify({ type: 'enable_mic' }));
        }
      } catch (error) {
        console.error('Failed to enable microphone:', error);
      }
    } else {
      stopListening();
      setMicEnabled(false);
      if (wsRef.current && isConnected) {
        wsRef.current.send(JSON.stringify({ type: 'disable_mic' }));
      }
    }
  };

  const clearConversation = () => {
    setConversationHistory([]);
    setCurrentResponse('');
    responseRef.current = '';
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #000000 0%, #1a1a1a 100%)',
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      justifyContent: 'center',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      {/* Connection Status */}
      <div style={{
        position: 'absolute',
        top: '20px',
        right: '20px',
        color: isConnected ? '#4CAF50' : '#FF5722',
        fontSize: '14px',
        fontWeight: 'bold'
      }}>
        {isConnected ? '‚óè Connected' : '‚óè Disconnected'}
      </div>

      {/* Jarvis Title */}
      <div style={{
        color: '#FFD700',
        fontSize: '48px',
        fontWeight: 'bold',
        marginBottom: '20px',
        textShadow: '0 0 20px rgba(255, 215, 0, 0.5)',
        fontFamily: 'Arial, sans-serif'
      }}>
        J.A.R.V.I.S
      </div>

      {/* Glassy Blob */}
      <div style={{ marginBottom: '30px' }}>
        <GlassyBlob
          mode={mode}
          intensity={intensity}
          size={450}
        />
      </div>

      {/* Status Display */}
      <div style={{
        color: '#FFD700',
        fontSize: '18px',
        marginBottom: '20px',
        textAlign: 'center',
        minHeight: '25px'
      }}>
        Status: {mode.charAt(0).toUpperCase() + mode.slice(1)}
        {mode === 'listening' && ` (${Math.round(audioLevel * 100)}%)`}
      </div>

      {/* Text Input Area */}
      <div style={{
        width: '100%',
        maxWidth: '600px',
        marginBottom: '20px'
      }}>
        <div style={{
          display: 'flex',
          gap: '10px',
          marginBottom: '10px'
        }}>
          <input
            type="text"
            value={userText}
            onChange={(e) => setUserText(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && sendTextMessage()}
            placeholder="Type your message to Jarvis..."
            style={{
              flex: 1,
              padding: '12px',
              fontSize: '16px',
              border: '2px solid #FFD700',
              borderRadius: '25px',
              background: 'rgba(0, 0, 0, 0.8)',
              color: 'white',
              outline: 'none'
            }}
          />
          <button
            onClick={sendTextMessage}
            disabled={!userText.trim() || !isConnected}
            style={{
              padding: '12px 24px',
              fontSize: '16px',
              background: isConnected && userText.trim() ? '#FFD700' : '#666',
              color: 'black',
              border: 'none',
              borderRadius: '25px',
              cursor: isConnected && userText.trim() ? 'pointer' : 'not-allowed',
              fontWeight: 'bold'
            }}
          >
            Send
          </button>
        </div>

        {/* Control Buttons */}
        <div style={{ display: 'flex', gap: '10px', justifyContent: 'center' }}>
          <button
            onClick={toggleMicrophone}
            style={{
              padding: '10px 20px',
              background: micEnabled ? '#4CAF50' : '#666',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            üé§ {micEnabled ? 'Mic ON' : 'Mic OFF'}
          </button>
          
          <button
            onClick={clearConversation}
            style={{
              padding: '10px 20px',
              background: '#FF5722',
              color: 'white',
              border: 'none',
              borderRadius: '20px',
              cursor: 'pointer',
              fontWeight: 'bold'
            }}
          >
            Clear History
          </button>
        </div>
      </div>

      {/* Current Response Display */}
      {currentResponse && (
        <div style={{
          width: '100%',
          maxWidth: '600px',
          padding: '15px',
          background: 'rgba(255, 215, 0, 0.1)',
          border: '1px solid #FFD700',
          borderRadius: '10px',
          color: 'white',
          marginBottom: '20px'
        }}>
          <strong>Jarvis:</strong> {currentResponse}
          <span style={{ animation: 'blink 1s infinite' }}>|</span>
        </div>
      )}

      {/* Conversation History */}
      <div style={{
        width: '100%',
        maxWidth: '600px',
        maxHeight: '300px',
        overflowY: 'auto',
        padding: '10px',
        background: 'rgba(255, 255, 255, 0.05)',
        borderRadius: '10px',
        border: '1px solid #333'
      }}>
        <h3 style={{ color: '#FFD700', marginTop: 0 }}>Conversation History</h3>
        {conversationHistory.length === 0 ? (
          <p style={{ color: '#666', fontStyle: 'italic' }}>No conversation yet...</p>
        ) : (
          conversationHistory.map((message, index) => (
            <div key={index} style={{
              marginBottom: '15px',
              padding: '10px',
              borderRadius: '5px',
              background: message.role === 'user' ? 'rgba(100, 150, 255, 0.1)' : 'rgba(255, 215, 0, 0.1)',
              border: `1px solid ${message.role === 'user' ? '#6496FF' : '#FFD700'}`
            }}>
              <div style={{
                fontSize: '12px',
                color: '#888',
                marginBottom: '5px'
              }}>
                {message.role === 'user' ? 'üë§ You' : 'ü§ñ Jarvis'} - {message.timestamp}
              </div>
              <div style={{ color: 'white' }}>
                {message.text}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Add blinking cursor animation */}
      <style>
        {`
          @keyframes blink {
            0%, 50% { opacity: 1; }
            51%, 100% { opacity: 0; }
          }
        `}
      </style>
    </div>
  );
};

