import React, { useState, useEffect, useRef } from 'react';
import GlassyBlob from './GlassyBlob';
import { useAudioAnalyzer } from './AudioAnalyzer';
import ChatPanel from './ChatPanel';

const JarvisInterface = () => {
  const [mode, setMode] = useState('idle');
  const [intensity, setIntensity] = useState(0.5);
  const [userText, setUserText] = useState('');
  const [assistantText, setAssistantText] = useState('');
  const [isConnected, setIsConnected] = useState(false);
  const [conversationHistory, setConversationHistory] = useState([]);
  const [currentResponse, setCurrentResponse] = useState('');
  const [micEnabled, setMicEnabled] = useState(false);
  
  const wsRef = useRef(null);
  const responseRef = useRef('');

  // Audio analyzer for real-time audio level monitoring
  const { startListening, stopListening, isListening, audioLevel } = useAudioAnalyzer(
    (level) => {
      if (mode === 'listening') {
        setIntensity(Math.max(0.1, level * 1.5)); // Scale and ensure minimum
      }
    }
  );

  // WebSocket connection to Python Jarvis backend
  useEffect(() => {
    connectToJarvis();
    
    return () => {
      if (wsRef.current) {
        wsRef.current.close();
      }
      stopListening();
    };
  }, []);

  const connectToJarvis = () => {
    try {
      wsRef.current = new WebSocket('ws://localhost:8765');
      
      wsRef.current.onopen = () => {
        console.log('Connected to Jarvis backend');
        setIsConnected(true);
      };

      wsRef.current.onmessage = (event) => {
        const data = JSON.parse(event.data);
        handleJarvisMessage(data);
      };

      wsRef.current.onclose = () => {
        console.log('Disconnected from Jarvis backend');
        setIsConnected(false);
        // Try to reconnect after 3 seconds
        setTimeout(connectToJarvis, 3000);
      };

      wsRef.current.onerror = (error) => {
        console.error('WebSocket error:', error);
        setIsConnected(false);
      };
    } catch (error) {
      console.error('Failed to connect to Jarvis:', error);
      setIsConnected(false);
    }
  };

  const handleJarvisMessage = (data) => {
    switch (data.type) {
      case 'user_text':
        // Only add to conversation, don't set userText (which is for input)
        addToConversation('user', data.text);
        break;
        
      case 'assistant_stream':
        responseRef.current += data.text;
        setCurrentResponse(responseRef.current);
        break;
        
      case 'assistant_final':
        setAssistantText(data.text);
        addToConversation('assistant', data.text);
        responseRef.current = '';
        setCurrentResponse('');
        setMode('idle');
        setIntensity(0.5);
        break;
        
      case 'mode_change':
        setMode(data.mode);
        if (data.mode === 'listening') {
          setIntensity(0.3);
          if (micEnabled) {
            startListening();
          }
        } else if (data.mode === 'speaking') {
          setIntensity(data.intensity || 0.8);
          stopListening();
        } else {
          setIntensity(0.5);
          stopListening();
        }
        break;
        
      case 'audio_level':
        if (data.mode === 'speaking') {
          setIntensity(Math.max(0.3, data.level * 1.2));
        }
        break;
        
      default:
        console.log('Unknown message type:', data.type);
    }
  };

  const addToConversation = (role, text) => {
    setConversationHistory(prev => [...prev, { role, text, timestamp: new Date().toLocaleTimeString() }]);
  };

  const sendTextMessage = () => {
    if (userText.trim() && wsRef.current && isConnected) {
      const message = userText.trim();
      
      // Send to backend - backend will handle adding to conversation
      wsRef.current.send(JSON.stringify({
        type: 'text_input',
        text: message
      }));
      
      // Clear input immediately
      setUserText('');
      
      // Mode changes will be handled by backend messages
    }
  };

  const toggleMicrophone = async () => {
    if (!micEnabled) {
      try {
        await startListening();
        setMicEnabled(true);
        if (wsRef.current && isConnected) {
          wsRef.current.send(JSON.stringify({ type: 'enable_mic' }));
        }
      } catch (error) {
        console.error('Failed to enable microphone:', error);
      }
    } else {
      stopListening();
      setMicEnabled(false);
      if (wsRef.current && isConnected) {
        wsRef.current.send(JSON.stringify({ type: 'disable_mic' }));
      }
    }
  };

  const clearConversation = () => {
    setConversationHistory([]);
    setCurrentResponse('');
    responseRef.current = '';
  };

  const [isChatOpen, setIsChatOpen] = useState(false);

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #000000 0%, #0a0a0a 100%)',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Inter, system-ui, sans-serif',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Connection Status */}
      <div style={{
        position: 'absolute',
        top: '20px',
        left: '20px',
        color: isConnected ? '#00ff88' : '#ff4757',
        fontSize: '12px',
        fontWeight: '500',
        display: 'flex',
        alignItems: 'center',
        gap: '8px',
        padding: '8px 12px',
        background: 'rgba(0, 0, 0, 0.3)',
        borderRadius: '20px',
        backdropFilter: 'blur(10px)'
      }}>
        <div style={{
          width: '8px',
          height: '8px',
          borderRadius: '50%',
          background: isConnected ? '#00ff88' : '#ff4757',
          boxShadow: `0 0 10px ${isConnected ? '#00ff88' : '#ff4757'}`
        }}></div>
        {isConnected ? 'Connected' : 'Disconnected'}
      </div>

      {/* Chat Toggle Button - Bottom Right */}
      <button
        onClick={() => setIsChatOpen(!isChatOpen)}
        style={{
          position: 'absolute',
          bottom: '30px',
          right: '30px',
          width: '60px',
          height: '60px',
          borderRadius: '50%',
          background: isChatOpen 
            ? 'rgba(255, 87, 87, 0.2)' 
            : 'rgba(64, 158, 255, 0.2)',
          border: `1px solid ${isChatOpen ? 'rgba(255, 87, 87, 0.4)' : 'rgba(64, 158, 255, 0.4)'}`,
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backdropFilter: 'blur(10px)',
          transition: 'all 0.3s ease',
          zIndex: 1000,
          boxShadow: isChatOpen 
            ? '0 0 20px rgba(255, 87, 87, 0.3)' 
            : '0 0 20px rgba(64, 158, 255, 0.3)'
        }}
        onMouseOver={(e) => {
          e.target.style.transform = 'scale(1.1)';
          e.target.style.background = isChatOpen 
            ? 'rgba(255, 87, 87, 0.3)' 
            : 'rgba(64, 158, 255, 0.3)';
        }}
        onMouseOut={(e) => {
          e.target.style.transform = 'scale(1)';
          e.target.style.background = isChatOpen 
            ? 'rgba(255, 87, 87, 0.2)' 
            : 'rgba(64, 158, 255, 0.2)';
        }}
      >
        {isChatOpen ? (
          <span style={{ color: '#FF5757', fontSize: '24px', fontWeight: 'bold' }}>✕</span>
        ) : (
          <div style={{ 
            width: '28px', 
            height: '28px', 
            position: 'relative',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
          }}>
            <div style={{
              width: '20px',
              height: '16px',
              border: '2px solid #409EFF',
              borderRadius: '4px',
              position: 'relative',
              backgroundColor: 'rgba(64, 158, 255, 0.1)'
            }}>
              <div style={{
                position: 'absolute',
                bottom: '-6px',
                left: '2px',
                width: '0',
                height: '0',
                borderLeft: '4px solid transparent',
                borderRight: '4px solid transparent',
                borderTop: '6px solid #409EFF'
              }} />
              <div style={{
                position: 'absolute',
                top: '3px',
                left: '3px',
                right: '3px',
                display: 'flex',
                justifyContent: 'space-between'
              }}>
                <div style={{ width: '2px', height: '2px', backgroundColor: '#409EFF', borderRadius: '50%' }} />
                <div style={{ width: '2px', height: '2px', backgroundColor: '#409EFF', borderRadius: '50%' }} />
                <div style={{ width: '2px', height: '2px', backgroundColor: '#409EFF', borderRadius: '50%' }} />
              </div>
            </div>
          </div>
        )}
      </button>

      {/* Professional Microphone Button - Bottom Center */}
      <button
        onClick={toggleMicrophone}
        style={{
          position: 'absolute',
          bottom: '30px',
          left: '50%',
          transform: 'translateX(-50%)',
          width: '70px',
          height: '70px',
          borderRadius: '50%',
          background: micEnabled 
            ? 'linear-gradient(135deg, #00ff88, #00d4aa)' 
            : 'rgba(36, 58, 82, 0.8)',
          border: micEnabled 
            ? '2px solid rgba(0, 255, 136, 0.4)' 
            : '2px solid rgba(255, 255, 255, 0.2)',
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backdropFilter: 'blur(10px)',
          transition: 'all 0.3s ease',
          boxShadow: micEnabled 
            ? '0 0 30px rgba(0, 255, 136, 0.4), 0 4px 15px rgba(0, 0, 0, 0.3)' 
            : '0 0 20px rgba(36, 58, 82, 0.5), 0 4px 15px rgba(0, 0, 0, 0.3)',
          zIndex: 1000
        }}
        onMouseOver={(e) => {
          e.target.style.transform = 'translateX(-50%) scale(1.1)';
        }}
        onMouseOut={(e) => {
          e.target.style.transform = 'translateX(-50%) scale(1)';
        }}
      >
        <div style={{ 
          width: '32px', 
          height: '32px', 
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'relative'
        }}>
          {/* Microphone body */}
          <div style={{
            width: '12px',
            height: '18px',
            backgroundColor: micEnabled ? '#000' : '#fff',
            borderRadius: '6px 6px 6px 6px',
            position: 'relative',
            border: micEnabled ? 'none' : '2px solid rgba(255, 255, 255, 0.8)'
          }}>
            {/* Microphone grille lines */}
            <div style={{
              position: 'absolute',
              top: '4px',
              left: '50%',
              transform: 'translateX(-50%)',
              width: '6px',
              height: '1px',
              backgroundColor: micEnabled ? '#00ff88' : 'rgba(255, 255, 255, 0.6)'
            }} />
            <div style={{
              position: 'absolute',
              top: '7px',
              left: '50%',
              transform: 'translateX(-50%)',
              width: '6px',
              height: '1px',
              backgroundColor: micEnabled ? '#00ff88' : 'rgba(255, 255, 255, 0.6)'
            }} />
            <div style={{
              position: 'absolute',
              top: '10px',
              left: '50%',
              transform: 'translateX(-50%)',
              width: '6px',
              height: '1px',
              backgroundColor: micEnabled ? '#00ff88' : 'rgba(255, 255, 255, 0.6)'
            }} />
          </div>
          {/* Microphone stand */}
          <div style={{
            position: 'absolute',
            bottom: '2px',
            left: '50%',
            transform: 'translateX(-50%)',
            width: '2px',
            height: '8px',
            backgroundColor: micEnabled ? '#000' : '#fff'
          }} />
          {/* Microphone base */}
          <div style={{
            position: 'absolute',
            bottom: '0px',
            left: '50%',
            transform: 'translateX(-50%)',
            width: '12px',
            height: '2px',
            backgroundColor: micEnabled ? '#000' : '#fff',
            borderRadius: '1px'
          }} />
        </div>
      </button>

      {/* Main Blob Area */}
      <div style={{
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        zIndex: 1
      }}>
        {/* Glassy Blob without circle */}
        <div style={{ 
          marginBottom: '30px',
          filter: 'drop-shadow(0 0 50px rgba(255, 215, 0, 0.3))'
        }}>
          <GlassyBlob
            mode={mode}
            intensity={intensity}
            size={500}
          />
        </div>

        {/* Minimalist Status Display */}
        <div style={{
          color: 'rgba(255, 255, 255, 0.7)',
          fontSize: '14px',
          fontWeight: '400',
          textAlign: 'center',
          textTransform: 'uppercase',
          letterSpacing: '2px',
          opacity: 0.8
        }}>
          {mode === 'idle' && 'Ready'}
          {mode === 'listening' && `Listening ${Math.round(audioLevel * 100)}%`}
          {mode === 'thinking' && 'Processing...'}
          {mode === 'speaking' && 'Speaking'}
        </div>
      </div>

      {/* Slide-out Chat Panel */}
      <ChatPanel
        isOpen={isChatOpen}
        conversationHistory={conversationHistory}
        currentResponse={currentResponse}
        onSendMessage={sendTextMessage}
        userText={userText}
        setUserText={setUserText}
        isConnected={isConnected}
        onClearHistory={clearConversation}
      />

      {/* Global Styles */}
      <style>
        {`
          @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700&display=swap');
          
          @keyframes blink {
            0%, 50% { opacity: 1; }
            51%, 100% { opacity: 0; }
          }
          
          @keyframes slideIn {
            from { transform: translateX(100%); }
            to { transform: translateX(0); }
          }
          
          @keyframes slideOut {
            from { transform: translateX(0); }
            to { transform: translateX(100%); }
          }
          
          * {
            box-sizing: border-box;
          }
          
          ::-webkit-scrollbar {
            width: 6px;
          }
          
          ::-webkit-scrollbar-track {
            background: rgba(255, 255, 255, 0.05);
            border-radius: 3px;
          }
          
          ::-webkit-scrollbar-thumb {
            background: rgba(255, 255, 255, 0.2);
            border-radius: 3px;
          }
          
          ::-webkit-scrollbar-thumb:hover {
            background: rgba(255, 255, 255, 0.3);
          }
        `}
      </style>
    </div>
  );
};

export default JarvisInterface;
