import React from 'react';

const ChatPanel = ({
  isOpen,
  conversationHistory,
  currentResponse,
  isWaitingForResponse,
  onSendMessage,
  userText,
  setUserText,
  isConnected,
  onClearHistory,
  onClose
}) => {
  return (
    <>
      {/* Backdrop */}
      {isOpen && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0, 0, 0, 0.5)',
            zIndex: 998,
            backdropFilter: 'blur(2px)',
            animation: 'fadeIn 0.3s ease'
          }}
        />
      )}

      {/* Chat Panel */}
      <div
        style={{
          position: 'fixed',
          top: 0,
          right: 0,
          width: '400px',
          height: '100vh',
          background: 'linear-gradient(180deg, #1e293b 0%, #0f172a 50%, #020617 100%)',
          border: 'none',
          zIndex: 999,
          display: 'flex',
          flexDirection: 'column',
          transform: isOpen ? 'translateX(0)' : 'translateX(100%)',
          transition: 'transform 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
          boxShadow: '0 0 50px rgba(0, 0, 0, 0.8)'
        }}
      >
        {/* Header */}
        <div style={{
          padding: '20px',
          borderBottom: '1px solid rgba(59, 130, 246, 0.3)',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          position: 'relative',
          background: 'rgba(30, 41, 59, 0.8)',
          backdropFilter: 'blur(10px)'
        }}>
          {/* Close Button - Top Left */}
          <button
            onClick={onClose}
            style={{
              position: 'absolute',
              top: '15px',
              left: '15px',
              width: '30px',
              height: '30px',
              borderRadius: '50%',
              background: 'rgba(255, 87, 87, 0.2)',
              border: '1px solid rgba(255, 87, 87, 0.4)',
              color: '#FF5757',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '16px',
              fontWeight: 'bold',
              transition: 'all 0.2s ease',
              zIndex: 1001
            }}
            onMouseOver={(e) => {
              e.target.style.background = 'rgba(255, 87, 87, 0.3)';
              e.target.style.transform = 'scale(1.1)';
            }}
            onMouseOut={(e) => {
              e.target.style.background = 'rgba(255, 87, 87, 0.2)';
              e.target.style.transform = 'scale(1)';
            }}
          >
            ✕
          </button>

          <h3 style={{
            color: 'rgba(255, 255, 255, 0.9)',
            margin: 0,
            fontSize: '16px',
            fontWeight: '600',
            letterSpacing: '0.5px',
            marginLeft: '40px' // Make space for close button
          }}>
            Conversation
          </h3>
          <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
            <button
              onClick={onClearHistory}
              style={{
                padding: '8px 12px',
                background: 'rgba(255, 87, 34, 0.2)',
                color: '#FF5722',
                border: '1px solid rgba(255, 87, 34, 0.3)',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '11px',
                fontWeight: '500',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
                transition: 'all 0.2s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(255, 87, 34, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(255, 87, 34, 0.2)';
              }}
            >
              Clear
            </button>
          </div>
        </div>

        {/* Messages Area */}
        <div style={{
          flex: 1,
          overflowY: 'auto',
          padding: '20px 15px',
          display: 'flex',
          flexDirection: 'column',
          gap: '12px'
        }}>
          {conversationHistory.length === 0 ? (
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              height: '100%',
              textAlign: 'center',
              color: 'rgba(148, 163, 184, 0.6)'
            }}>
              <div style={{
                fontSize: '48px',
                marginBottom: '16px',
                opacity: 0.4
              }}>
                💬
              </div>
              <p style={{ margin: 0, fontSize: '14px' }}>
                Start a conversation with JARVIS
              </p>
            </div>
          ) : (
            <>
              {conversationHistory.map((message, index) => (
                <div
                  key={index}
                  style={{
                    display: 'flex',
                    justifyContent: message.role === 'user' ? 'flex-start' : 'flex-end',
                    marginBottom: '8px'
                  }}
                >
                  <div style={{
                    maxWidth: '85%',
                    padding: '12px 16px',
                    borderRadius: message.role === 'user'
                      ? '20px 20px 20px 5px'  // Stretched from left like in image
                      : '20px 20px 5px 20px', // Stretched from right like in image
                    background: message.role === 'user'
                      ? 'rgba(59, 130, 246, 0.8)'  // Blue for user (left side)
                      : 'rgba(148, 163, 184, 0.9)', // Light gray for assistant (right side)
                    color: 'white',
                    fontSize: '14px',
                    lineHeight: '1.4',
                    wordWrap: 'break-word',
                    backdropFilter: 'blur(10px)',
                    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.3)'
                  }}>
                    {message.text}
                  </div>
                </div>
              ))}

              {/* Loading Dots - Show when waiting for response */}
              {isWaitingForResponse && !currentResponse && (
                <div style={{
                  display: 'flex',
                  justifyContent: 'flex-end',
                  marginBottom: '8px'
                }}>
                  <div style={{
                    maxWidth: '85%',
                    padding: '12px 16px',
                    borderRadius: '20px 20px 5px 20px',
                    background: 'rgba(148, 163, 184, 0.9)',
                    color: 'white',
                    fontSize: '14px',
                    lineHeight: '1.4',
                    opFilter: 'blur(10px)',
                    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.3)',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '4px'
                  }}>
                    <div className="loading-dots">
                      <div className="dot"></div>
                      <div className="dot"></div>
                      <div className="dot"></div>
                    </div>
                  </div>
                </div>
              )}

              {/* Current Response */}
              {currentResponse && (
                <div style={{
                  display: 'flex',
                  justifyContent: 'flex-end',
                  marginBottom: '8px'
                }}>
                  <div style={{
                    maxWidth: '85%',
                    padding: '12px 16px',
                    borderRadius: '20px 20px 5px 20px',
                    background: 'rgba(148, 163, 184, 0.9)',
                    color: 'white',
                    fontSize: '14px',
                    lineHeight: '1.4',
                    wordWrap: 'break-word',
                    backdropFilter: 'blur(10px)',
                    boxShadow: '0 2px 8px rgba(0, 0, 0, 0.3)'
                  }}>
                    {currentResponse}
                    <span style={{
                      animation: 'blink 1s infinite',
                      marginLeft: '2px',
                      color: 'rgba(255, 255, 255, 0.7)'
                    }}>|</span>
                  </div>
                </div>
              )}
            </>
          )}
        </div>

        {/* Input Area */}
        <div style={{
          padding: '15px 20px 25px 20px',
          background: 'rgba(30, 41, 59, 0.8)',
          backdropFilter: 'blur(10px)'
        }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            gap: '12px',
            background: 'rgba(148, 163, 184, 0.9)',
            borderRadius: '25px',
            padding: '8px 8px 8px 20px',
            boxShadow: '0 4px 15px rgba(0, 0, 0, 0.3)'
          }}>
            <input
              type="text"
              value={userText}
              onChange={(e) => setUserText(e.target.value)}
              onKeyPress={(e) => {
                if (e.key === 'Enter' && !e.shiftKey) {
                  e.preventDefault();
                  onSendMessage();
                }
              }}
              placeholder="Type a message..."
              style={{
                flex: 1,
                background: 'transparent',
                border: 'none',
                outline: 'none',
                color: 'white',
                fontSize: '14px',
                fontFamily: 'Inter, system-ui, sans-serif',
                '::placeholder': {
                  color: 'rgba(255, 255, 255, 0.6)'
                }
              }}
            />
            <button
              onClick={onSendMessage}
              disabled={!userText.trim() || !isConnected}
              style={{
                width: '40px',
                height: '40px',
                borderRadius: '50%',
                background: (isConnected && userText.trim())
                  ? 'linear-gradient(135deg, #3b82f6, #1d4ed8)'
                  : 'rgba(255, 255, 255, 0.3)',
                color: 'white',
                border: 'none',
                cursor: (isConnected && userText.trim()) ? 'pointer' : 'not-allowed',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '16px',
                transition: 'all 0.2s ease',
                flexShrink: 0,
                boxShadow: '0 2px 8px rgba(0, 0, 0, 0.3)'
              }}
              onMouseOver={(e) => {
                if (isConnected && userText.trim()) {
                  e.target.style.transform = 'scale(1.05)';
                }
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'scale(1)';
              }}
            >
              ▶
            </button>
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
          }
          
          @keyframes loadingDots {
            0%, 20% {
              opacity: 0.4;
              transform: scale(0.8);
            }
            50% {
              opacity: 1;
              transform: scale(1.2);
            }
            80%, 100% {
              opacity: 0.4;
              transform: scale(0.8);
            }
          }
          
          .loading-message-container {
            display: flex;
            flex-direction: column;
            align-items: flex-start;
            margin-bottom: 12px;
          }
          
          .loading-message-bubble {
            max-width: 80%;
            padding: 14px 18px;
            border-radius: 20px;
            background: rgba(255, 255, 255, 0.9);
            color: #333;
            font-size: 14px;
            line-height: 1.5;
            backdropFilter: blur(10px);
            boxShadow: 0 4px 15px rgba(0, 0, 0, 0.2);
            position: relative;
            min-height: 20px;
            display: flex;
            align-items: center;
          }
          
          .loading-dots {
            display: flex;
            align-items: center;
            gap: 4px;
          }
          
          .loading-dots .dot {
            width: 6px;
            height: 6px;
            border-radius: 50%;
            background-color: rgba(255, 255, 255, 0.8);
            animation: loadingDots 1.5s ease-in-out infinite;
          }
          
          .loading-dots .dot:nth-child(1) {
            animation-delay: 0s;
          }
          
          .loading-dots .dot:nth-child(2) {
            animation-delay: 0.3s;
          }
          
          .loading-dots .dot:nth-child(3) {
            animation-delay: 0.6s;
          }
        `}
      </style>
    </>
  );
};

export default ChatPanel;
