import React from 'react';

const ChatPanel = ({
  isOpen,
  conversationHistory,
  currentResponse,
  onSendMessage,
  userText,
  setUserText,
  isConnected,
  onClearHistory,
  onClose
}) => {
  return (
    <>
      {/* Backdrop */}
      {isOpen && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: 'rgba(0, 0, 0, 0.5)',
            zIndex: 998,
            backdropFilter: 'blur(2px)',
            animation: 'fadeIn 0.3s ease'
          }}
        />
      )}

      {/* Chat Panel */}
      <div
        style={{
          position: 'fixed',
          top: 0,
          right: 0,
          width: '400px',
          height: '100vh',
          background: 'linear-gradient(135deg, rgba(10, 10, 15, 0.95), rgba(20, 20, 30, 0.95))',
          backdropFilter: 'blur(20px)',
          border: '1px solid rgba(255, 255, 255, 0.1)',
          borderRight: 'none',
          zIndex: 999,
          display: 'flex',
          flexDirection: 'column',
          transform: isOpen ? 'translateX(0)' : 'translateX(100%)',
          transition: 'transform 0.3s cubic-bezier(0.4, 0, 0.2, 1)',
          boxShadow: '0 0 50px rgba(0, 0, 0, 0.5)'
        }}
      >
        {/* Header */}
        <div style={{
          padding: '20px',
          borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          position: 'relative'
        }}>
          {/* Close Button - Top Left */}
          <button
            onClick={onClose}
            style={{
              position: 'absolute',
              top: '15px',
              left: '15px',
              width: '30px',
              height: '30px',
              borderRadius: '50%',
              background: 'rgba(255, 87, 87, 0.2)',
              border: '1px solid rgba(255, 87, 87, 0.4)',
              color: '#FF5757',
              cursor: 'pointer',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              fontSize: '16px',
              fontWeight: 'bold',
              transition: 'all 0.2s ease',
              zIndex: 1001
            }}
            onMouseOver={(e) => {
              e.target.style.background = 'rgba(255, 87, 87, 0.3)';
              e.target.style.transform = 'scale(1.1)';
            }}
            onMouseOut={(e) => {
              e.target.style.background = 'rgba(255, 87, 87, 0.2)';
              e.target.style.transform = 'scale(1)';
            }}
          >
            ✕
          </button>
          
          <h3 style={{
            color: 'rgba(255, 255, 255, 0.9)',
            margin: 0,
            fontSize: '16px',
            fontWeight: '600',
            letterSpacing: '0.5px',
            marginLeft: '40px' // Make space for close button
          }}>
            Conversation
          </h3>
          <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
            <button
              onClick={onClearHistory}
              style={{
                padding: '8px 12px',
                background: 'rgba(255, 87, 34, 0.2)',
                color: '#FF5722',
                border: '1px solid rgba(255, 87, 34, 0.3)',
                borderRadius: '6px',
                cursor: 'pointer',
                fontSize: '11px',
                fontWeight: '500',
                textTransform: 'uppercase',
                letterSpacing: '0.5px',
                transition: 'all 0.2s ease'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(255, 87, 34, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(255, 87, 34, 0.2)';
              }}
            >
              Clear
            </button>
          </div>
        </div>

        {/* Messages Area */}
        <div style={{
          flex: 1,
          overflowY: 'auto',
          padding: '20px',
          display: 'flex',
          flexDirection: 'column',
          gap: '16px'
        }}>
          {conversationHistory.length === 0 ? (
            <div style={{
              display: 'flex',
              flexDirection: 'column',
              alignItems: 'center',
              justifyContent: 'center',
              height: '100%',
              textAlign: 'center',
              color: 'rgba(255, 255, 255, 0.4)'
            }}>
              <div style={{
                fontSize: '48px',
                marginBottom: '16px',
                opacity: 0.3
              }}>
                💬
              </div>
              <p style={{ margin: 0, fontSize: '14px' }}>
                Start a conversation with JARVIS
              </p>
              <div style={{
                marginTop: '20px',
                padding: '12px',
                background: 'rgba(255, 255, 255, 0.05)',
                borderRadius: '8px',
                fontSize: '12px',
                color: 'rgba(255, 255, 255, 0.6)'
              }}>
                <div>• Ask JARVIS to help with images or similar questions</div>
                <div>• Show me objects, documents, or scenes you want analyzed</div>
                <div>• Ask me to identify objects, read text, or describe the scene</div>
              </div>
            </div>
          ) : (
            <>
              {conversationHistory.map((message, index) => (
                <div
                  key={index}
                  style={{
                    display: 'flex',
                    flexDirection: 'column',
                    alignItems: message.role === 'user' ? 'flex-end' : 'flex-start',
                    marginBottom: '8px'
                  }}
                >
                  <div style={{
                    maxWidth: '85%',
                    padding: '12px 16px',
                    borderRadius: '18px',
                    background: message.role === 'user' 
                      ? 'linear-gradient(135deg, #007AFF, #0056CC)'
                      : 'rgba(255, 255, 255, 0.1)',
                    color: 'white',
                    fontSize: '14px',
                    lineHeight: '1.4',
                    wordWrap: 'break-word',
                    border: message.role === 'user' ? 'none' : '1px solid rgba(255, 255, 255, 0.1)'
                  }}>
                    {message.text}
                  </div>
                  <div style={{
                    fontSize: '10px',
                    color: 'rgba(255, 255, 255, 0.4)',
                    marginTop: '4px',
                    marginLeft: message.role === 'user' ? '0' : '16px',
                    marginRight: message.role === 'user' ? '16px' : '0'
                  }}>
                    {message.timestamp}
                  </div>
                </div>
              ))}

              {/* Current Response */}
              {currentResponse && (
                <div style={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'flex-start',
                  marginBottom: '8px'
                }}>
                  <div style={{
                    maxWidth: '85%',
                    padding: '12px 16px',
                    borderRadius: '18px',
                    background: 'rgba(255, 255, 255, 0.1)',
                    color: 'white',
                    fontSize: '14px',
                    lineHeight: '1.4',
                    border: '1px solid rgba(255, 255, 255, 0.1)',
                    position: 'relative'
                  }}>
                    {currentResponse}
                    <span style={{ 
                      animation: 'blink 1s infinite',
                      marginLeft: '2px'
                    }}>|</span>
                  </div>
                </div>
              )}
            </>
          )}
        </div>

        {/* Input Area */}
        <div style={{
          padding: '20px',
          borderTop: '1px solid rgba(255, 255, 255, 0.1)',
          background: 'rgba(0, 0, 0, 0.2)'
        }}>
          <div style={{
            display: 'flex',
            gap: '10px',
            alignItems: 'flex-end'
          }}>
            <div style={{
              flex: 1,
              position: 'relative'
            }}>
              <textarea
                value={userText}
                onChange={(e) => setUserText(e.target.value)}
                onKeyPress={(e) => {
                  if (e.key === 'Enter' && !e.shiftKey) {
                    e.preventDefault();
                    onSendMessage();
                  }
                }}
                placeholder="Type a message..."
                style={{
                  width: '100%',
                  minHeight: '44px',
                  maxHeight: '120px',
                  padding: '12px 16px',
                  fontSize: '14px',
                  border: '1px solid rgba(255, 255, 255, 0.2)',
                  borderRadius: '22px',
                  background: 'rgba(255, 255, 255, 0.1)',
                  color: 'white',
                  outline: 'none',
                  resize: 'none',
                  fontFamily: 'Inter, system-ui, sans-serif'
                }}
                onFocus={(e) => {
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.4)';
                }}
                onBlur={(e) => {
                  e.target.style.borderColor = 'rgba(255, 255, 255, 0.2)';
                }}
              />
            </div>
            <button
              onClick={onSendMessage}
              disabled={!userText.trim() || !isConnected}
              style={{
                width: '44px',
                height: '44px',
                borderRadius: '50%',
                background: (isConnected && userText.trim()) 
                  ? 'linear-gradient(135deg, #007AFF, #0056CC)' 
                  : 'rgba(255, 255, 255, 0.1)',
                color: (isConnected && userText.trim()) ? 'white' : 'rgba(255, 255, 255, 0.4)',
                border: 'none',
                cursor: (isConnected && userText.trim()) ? 'pointer' : 'not-allowed',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                fontSize: '18px',
                transition: 'all 0.2s ease',
                flexShrink: 0
              }}
              onMouseOver={(e) => {
                if (isConnected && userText.trim()) {
                  e.target.style.transform = 'scale(1.05)';
                }
              }}
              onMouseOut={(e) => {
                e.target.style.transform = 'scale(1)';
              }}
            >
              ✈️
            </button>
          </div>
        </div>
      </div>

      <style>
        {`
          @keyframes fadeIn {
            from { opacity: 0; }
            to { opacity: 1; }
          }
        `}
      </style>
    </>
  );
};

export default ChatPanel;
